    const [list, setList] = useState([])


    const CopyText = (event) => {
        console.log(event.target.value)
        setItems(event.target.value)

    }
    const addList = () => {
        setList((oldList) => {
            return [...oldList, items]
        })
        setItems('')
    }


    const deleteItem = (itemid) => {
