import React, { useState } from 'react'
import AddBoxRounded from '@material-ui/icons/AddBoxRounded';
import ArrowBackIosRounded from '@material-ui/icons/ArrowBackIosRounded'
import Addtolist from './Addtolist'
import Form from './Form';
import ReactDom from 'react-dom';

export default function Todo() {
    const [items, setItems] = useState()
    const [list, setList] = useState([])


    const CopyText = (event) => {
        console.log(event.target.value)
        setItems(event.target.value)

    }
    const addList = () => {
        setList((oldList) => {
            return [...oldList, items]
        })
        setItems('')
    }


    const deleteItem = (itemid) => {
        console.log('deleted', itemid)

        setList((oldList) => {
            return oldList.filter((currElement, index) => {
                return index !== itemid;
            })
        })
    }

const goBack=()=>{
  ReactDom.render(
            <>
                <Form />
            </>,
            document.getElementById('root')
        );
}


    return (
        <div>
            <h1>Welcome to To-Do App</h1>

            <input type='text' className='form' value={items} name='item' placeholder='Enter the Item' onChange={CopyText}></input>
            <button className='add' onClick={addList}>
                <AddBoxRounded />
            </button><br /><br />
            {list.map((litem, index) => {
                let count = index + 1;
                return <Addtolist
                    key={index}
                    id={index}
                    item={litem}
                    onSelect={deleteItem}
                />
            })}
            <button className='back' onClick={goBack}><ArrowBackIosRounded /></button>
        </div>
    )
}
