import { useState } from "react"
import React from 'react'

export default function Form() {

    const [inputs, setName] = useState({
        firstname: "",
        lastname: "",
        email: "",
        phone: "",
    })


    const Copytext = (event) => {
        const inName = event.target.name
        const inValue = event.target.value
        /* console.log(inputs.firstname)
         console.log(inputs.lastname)*/
        setName((preValue) => {
            if (inName === 'fname') {

                return { array:
                    {firstname: inValue,
                    lastname: preValue.lastname,
                    email: preValue.email,
                    phone: preValue.phone,}

                }

            }
            else if (inName === 'lname') {

                return {
                    ...array,lastname: inValue,
                }

            }

            else if (inName === 'email') {

                return {
                    firstname: preValue.firstname,
                    lastname: preValue.lastname,
                    email: inValue,
                    phone: preValue.phone,
                }

            }

            else if (inName === 'phone') {

                return {
                    firstname: preValue.firstname,
                     lastname: preValue.lastname,
                    email: preValue.email,
                    phone: inValue,
                }

            }
        })

    }

    /*
        const Addtxt = () => {
             if (inName == 'fname') {
                console.log(inValue)
                console.log('First Name')
                setName(inputs.firstname)
            }
            else if (inName == 'lname') {
                 console.log(inValue)
                 console.log('Lastname')
                setName(inputs.lastname)
            }
        }*/
    return (
        <>
            <div>
                <h1>Hello  {inputs.firstname} {inputs.lastname}</h1>
                <h4>{inputs.email }</h4>
                <h5>{ inputs.phone}</h5>
                <input  value={inputs.firstname} name='fname' onChange={Copytext} className='form-control form-control-lg' placeholder='Enter Firstname' />
                <br /><br />
                <input  value={inputs.lastname} name='lname' onChange={Copytext} className='form-control form-control-lg' placeholder='Enter Lastname' />
                <br /><br />
                <input  value={inputs.email} name='email' onChange={Copytext} className='form-control form-control-lg' placeholder='Email' />
                <br /><br />
                <input  value={inputs.phone} name='phone' onChange={Copytext} className='form-control form-control-lg' placeholder='Phone' />
                <br /><br />
                <button className='btn btn-primary btn-lg' >Click me</button>
            </div>
        </>
    )
}
