import { useState } from "react"
import React from 'react'

export default function Form() {

    const [inputs, setName] = useState({
        firstname: "",
        lastname: "",
    })


    const Copytext = (event) => {
        const inName = event.target.name
        const inValue = event.target.value
        console.log(fname)
        console.log(lname)
        setName((preValue) => {
            if (inName === 'fname') {

                return {
                    firstname: inValue,
                    lastname: preValue.lastname,
                }

            }
            else if (inName === 'lname') {

                return {
                    firstname: preValue.firstname,
                    lastname: inValue,
                }

            }
        })

    }

    /*
        const Addtxt = () => {
             if (inName == 'fname') {
                console.log(inValue)
                console.log('First Name')
                setName(inputs.firstname)
            }
            else if (inName == 'lname') {
                 console.log(inValue)
                 console.log('Lastname')
                setName(inputs.lastname)
            }
        }*/
    return (
        <>
            <div>
                <h1>Hello  {inputs.firstname} {inputs.lastname}</h1>
                <input type='text' name='fname' onChange={Copytext} className='form-control form-control-lg' placeholder='Enter name' />
                <br /><br />
                <input type='text' name='lname' onChange={Copytext} className='form-control form-control-lg' placeholder='Enter password' />
                <br /><br />
                <button className='btn btn-primary btn-lg' >Click me</button>
            </div>
        </>
    )
}
