import { useState } from "react"
import React from 'react'

export default function Form() {

    const [inputs, setName] = useState({
        firstname: "",
        lastname: "",
        email: "",
        phone: "",
    })


    const Copytext = (event) => {
console.log(event)
        const { name, value } = event.target
console.log(name,value)

        setName((preValue) => {
            console.log(preValue)
            return {
                ...preValue,
                [name]: value,
            }
        })

    }

    /*
        const Addtxt = () => {
             if (inName == 'fname') {
                console.log(inValue)
                console.log('First Name')
                setName(inputs.firstname)
            }
            else if (inName == 'lname') {
                 console.log(inValue)
                 console.log('Lastname')
                setName(inputs.lastname)
            }
        }*/
    return (
        <>
            <div>
                <h1>Hello  {inputs.firstname} {inputs.lastname}</h1>
                <h4>{inputs.email}</h4>
                <h5>{inputs.phone}</h5>
                <input value={inputs.firstname} name='firstname' onChange={Copytext} className='form-control form-control-lg' placeholder='Enter Firstname' />
                <br /><br />
                <input value={inputs.lastname} name='lastname' onChange={Copytext} className='form-control form-control-lg' placeholder='Enter Lastname' />
                <br /><br />
                <input value={inputs.email} name='email' onChange={Copytext} className='form-control form-control-lg' placeholder='Email' />
                <br /><br />
                <input value={inputs.phone} name='phone' onChange={Copytext} className='form-control form-control-lg' placeholder='Phone' />
                <br /><br />
                <button className='btn btn-primary btn-lg' >Click me</button>
            </div>
        </>
    )
}
