import React, { useState } from 'react'

export default function Todo() {
    const [items, setItems] = useState()
    const [list, setList] = useState([])
    const [dellist, setDellist] = useState(list)

    const CopyText = (event) => {
        console.log(event.target.value)
        setItems(event.target.value)

    }
    const addList = () => {
        setList((oldList) => {
            return [...oldList, items]
        })
        setItems('')
    }


    const del = (index) => {
        console.log('Clicked')
  /*      setItems((oldList) => {
            list.map((listitem, index) => {
                list.splice(index, 1)
                return (list)
            })
        })*/
    }


    return (
        <div>
            <h1>Welcome to To-Do App</h1>

            <input type='text' className='form' value={items} name='item' placeholder='Enter the Item' onChange={CopyText}></input>
            <button className='add' onClick={addList}>
                +
            </button><br /><br />
            {list.map((litem, index) => {
                let count = index + 1;
                return [<h5>{count} ) {litem} <button onClick={del}>Delete</button></h5>]
            })}

        </div>
    )
}
