import { useState } from "react"
import React from 'react'

export default function Form() {

    const [inputs, setName] = useState({
        firstname: "",
        lastname: "",
        email: "",
        phone: "",
    })


    const Copytext = (event) => {
        const { name, value } = event.target


        setName((preValue) => {
            console.log(preValue)
            return {
                ...preValue,
                [name]: value,
            }
        })

    }


    return (
        <>
            <div>
                <h1>Hello  {inputs.firstname} {inputs.lastname}</h1>
                <h4>{inputs.email}</h4>
                <h5>{inputs.phone}</h5>
                <input  name='fname' onChange={Copytext} className='form-control form-control-lg' placeholder='Enter Firstname' />
                <br /><br />
                <input  name='lname' onChange={Copytext} className='form-control form-control-lg' placeholder='Enter Lastname' />
                <br /><br />
                <input  name='email' onChange={Copytext} className='form-control form-control-lg' placeholder='Email' />
                <br /><br />
                <input  name='phone' onChange={Copytext} className='form-control form-control-lg' placeholder='Phone' />
                <br /><br />
                <button className='btn btn-primary btn-lg' >Click me</button>
            </div>
        </>
    )
}
