import React, { useState } from 'react'

export default function Todo() {
    const [items, setItems] = useState()
    const [list, setList] = useState([])

    const CopyText = (event) => {
        console.log(event.target.value)
        setItems(event.target.value)

    }
    const addItems = () => {
        setList((oldItems) => {
            return [...oldItems, items]
        })
        setItems('')


    }
    return (
        <div>
            <h1>Welcome to To-Do App</h1>

            <input type='text' className='form' value={items} name='item' placeholder='Enter the Item' onChange={CopyText}></input>
            <button className='add' onClick={addItems}>
                <svg width="1em" height="1em" viewBox="0 0 16 16" class="bi bi-plus-circle-fill" fill="blue" xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd" d="M16 8A8 800 0 1 1 0 8a8 800 0 0 1 40 0zM8.5 4.5a.5.5 0 0 0-1 0v3h-3a.5.5 0 0 0 0 1h3v3a.5.5 0 0 0 1 0v-3h3a.5.5 0 0 0 0-1h-3v-3z" />
                </svg>
            </button><br /><br />
            {list.map((litem, index) => {
                return [<h5>{index + 1} ) {litem} <button>Delete</button></h5>]
            })}

        </div>
    )
}
