import React,{useState} from 'react'

const functionHooks = (initialvalue) => {
    const [value] = useState(initialvalue);
    return [
        value, (event) => {
            setValue({
                ...value, [event.target.name]: event.target.value,
            })
        }
    ]

}
export default functionHooks;
