import React, { Component } from 'react'

class Todo extends Component {
    constructor() {
        super();
        this.state = {
            list: ['Mango', 'Apple', 'Orange'],
            newText: ""
        };

    }

    Adding = (event) => {
        let currentText = this.state.newText
        let existingText = this.state.list
        existingText.push(currentText)
        this.setState({
            list: existingText
        })
    }


    Deleting = (event) => {
        let updatedList = this.state.list.splice(event, 1)
        this.setState({
            list: updatedList
        })
    }

    txtChange = (event) => {
        this.setState({ newText: event.target.value });
    }



    render() {
        return (
            <div>
                <h1>To-do List</h1>
                <input type='text' onChange={this.txtChange} placeholder='Enter the Element' />
                <button className='btn btn-outline-success' onClick={this.Adding}>Add</button><br /><br />
                {
                    this.state.list.map((itm, k) => {
                        return (
                            <li>{itm} <button className='btn btn-outline-info' onClick={() => {
                                this.Deleting(k)
                            }}>Delete</button></li>
                        )
                    })
                }
                <p>{this.state.newText}</p>
            </div>
        )
    }
}

export default Todo;
