import React from 'react'

functionHooks = (initialvalue) => {
    const [value] = React.useState(initialvalue)
    return [
        value, (event) => {
            setValue({
                ...value, [event.target.name]: event.target.value,
            })
        }
    ]

}
export default functionHooks;
