import React, { Component } from 'react'

class Todo extends Component {
    constructor() {
        super();
        this.state = {
            list: ['Mango', 'Apple', 'Orange'],
            newText: ""
        };

    }

    /*Adding = () => {

    }*/


    txtChange = (event) => {
        this.setState({newtext:event.target.value});
    }



    render() {
        return (
            <div>
                <h1>To-do List</h1>
                <input type='text' onChange={this.state.txtChange} id='name' placeholder='Name' />
                <button className='btn btn-outline-success' onClick={this.state.Adding}>Add</button><br /><br />
                {
                    this.state.list.map((itm, k) => {
                        return (
                            <li>{itm} <button className='btn btn-outline-info'>Delete</button></li>
                        )
                    })
                }
<p>{this.state.newText}</p>
            </div>
        )
    }
}

export default Todo;
